                          "pattern": "^[1-9][0-9]*(m|\\.\\d+)?$",
                          "default": "1.0",
                          "x-ui-description": "<mds-key>",
                          "x-ui-validation-message": "<mds-key>"
                        }
                      },
                      "type": "object",
                      "additionalProperties": false
                    },
                    "requests": {
                      "properties": {
                        "memory": {
                          "description": "memory request of arms prometheus operator",
                          "type": "string",
                          "pattern": "^[1-9][0-9]*(\\.\\d+)?(K|Ki|M|Mi|G|Gi|T|Ti)?$",
                          "default": "500m",
                          "x-ui-description": "<mds-key>",
                          "x-ui-validation-message": "<mds-key>"
                        },
                        "cpu": {
                          "description": "cpu request of arms prometheus operator",
                          "type": "string",
                          "pattern": "^[1-9][0-9]*(m|\\.\\d+)?$",
                          "default": "1.0",
                          "x-ui-description": "<mds-key>",
                          "x-ui-validation-message": "<mds-key>"
                        }
                      },
                      "type": "object",
                      "additionalProperties": false
                    }
                  },
                  "type": "object",
                  "additionalProperties": false
                }
              },
              "type": "object",
              "additionalProperties": false
            }
          },
          "type": "object",
          "additionalProperties": false
        }
      },
      "type": "object",
      "additionalProperties": false
    }
  },
  "title": "Values",
  "type": "object",
  "additionalProperties": false
}

