                "segmentDetails": [
                    {
                        "audioSegmentUrl": "http://xxx-ai-file.oss-cn-beijing.aliyuncs.com/202506021536413361/segment_0.mp3?OSSAccessKeyId=LTAI5tPLWJfJHNkZbfnQ****&Expires=1748854445&Signature=cWk2X%2B7MtbIEm%2Fu1mwVts59hpwU%3D",
                        "index": 0,
